<?php   ?><?php  if (!isset($customChatStatus) || $customChatStatus == false) : ?><?php  if ($is_activated == true || $is_proactive_based == true) : ?><?php  if ($chat->status == erLhcoreClassModelChat::STATUS_ACTIVE_CHAT && ($user = $chat->user) !== false) : ?><?php   ?><div class="operator-info float-break"><div class="pull-left pr5"><?php  if ($user->has_photo) : ?><img src="<?php  echo $user->photo_path?>" alt="<?php  echo htmlspecialchars($user->name_support)?>" /><?php  else : ?><i class="icon-assistant material-icons">account_box</i><?php  endif;?></div><div class="pl10"><div><strong><?php  echo htmlspecialchars($user->name_support)?></strong></div><?php  if (isset($extraMessage)) : ?><i><?php  echo $extraMessage;?></i><?php  endif;?><?php   ?><?php  if (!isset($hideThumbs) || $hideThumbs == false) : ?><?php  if (!isset($theme) || $theme === false || $theme->show_voting == 1) : ?><i class="material-icons<?php  if ($chat->fbst == 1) : ?> up-voted<?php  endif;?> up-vote-action" role="button" data-id="1" onclick="lhinst.voteAction($(this))" >thumb_up</i><i class="material-icons<?php  if ($chat->fbst == 2) : ?> down-voted<?php  endif;?> down-vote-action" role="button" data-id="2" onclick="lhinst.voteAction($(this))">thumb_down</i><?php  endif;?><?php  if ($user->skype != '') : ?><a href="skype:<?php  echo htmlspecialchars($user->skype)?>?call" class="material-icons" title="Skype call">phone_in_talk</a><?php  endif;?><?php  endif;?><?php   ?></div></div><?php  elseif ($is_proactive_based == true) : ?><h4><?php  if ($theme !== false && $theme->support_joined != '') : ?><?php  echo htmlspecialchars($theme->support_joined)?><?php  else : ?><?php  if ($chat->transfer_uid > 0 && erLhcoreClassModelTransfer::getCount(array('filter' => array('chat_id' => $chat->id))) > 0) : ?>You have been transferred. Please wait for operator to join a chat...<?php  else : ?>A support staff member has joined this chat<?php  endif ?><?php  endif;?></h4><?php  endif;?><?php  elseif ($is_closed == true) : ?><h4><?php  if ($theme !== false && $theme->support_closed != '') : ?><?php  echo htmlspecialchars($theme->support_closed)?><?php  else : ?>A support staff member has closed this chat<?php  endif; ?></h4><?php  elseif ($chat->status == erLhcoreClassModelChat::STATUS_BOT_CHAT) : ?><h4><?php  if ($theme !== false && $theme->bot_status_text != '') : ?><?php  echo htmlspecialchars($theme->bot_status_text)?><?php  else : ?>You are talking with out bot now.<?php  endif; ?></h4><?php  elseif ($is_online == true) : ?><h4><?php  if ($chat->number_in_queue > 1) : ?> You are number <b><?php  echo $chat->number_in_queue?></b> in the queue. Please wait...<?php  else : ?><?php  if ($theme !== false && $theme->pending_join != '') : ?><?php  echo htmlspecialchars($theme->pending_join)?><?php  else : ?>Pending a support staff member to join, you can write your questions, and as soon as a support staff member confirms this chat, he will get your messages<?php  endif;?><?php  endif;?></h4><?php  else : ?><h4><?php  if ($theme !== false && $theme->noonline_operators != '') : ?><?php  echo htmlspecialchars($theme->noonline_operators)?><?php  else : ?>At this moment there are no logged in support staff members, but you can leave your messages<?php  endif;?></h4><?php  endif; ?><?php  endif; ?>